import client from '../client'
import Header from '../components/Header'

const Index = (props) => {
    const { posts: [] } = props
    return (
        <div>
            <Header />
            <h1>Welcome to my blog!</h1>

            {posts.map(
                ({ _id, title = '', slug = '', _createdAt = '' }) => (
                    <li key={_id}>
                        <Link
                            href='/post[slug]'
                        >

                        </Link>
                    </li>
                )
            )}
        </div>
    )
}

Index.getInitialProps = async () => ({
    posts: await client.fetch(`
        *[_type == "post"]
    `)
})
export default Index
