import client from '../client'
import Link from 'next/link'
import HeaderLayout from '../components/HeaderLayout'
import KeywordTags from '../components/KeywordTags'
import styled from 'styled-components'
import moment from 'moment'
import '../globalStyles.css'

const ListItem = styled.li`
    margin: 1em 0;
    font-size: 1.75em;
`
const DateP = styled.p`
    font-size: .85em;
`

const Index = (props) => {
    console.log('props: ', props)
    const propsArray = Object.values(props)
    // last item in props is next.js url metadata
    const posts = propsArray.slice(
        0, propsArray.length - 1
    )
    // console.log('posts: ', posts)

    const tagCounts = posts.reduce((tagsObj, post) => {
        // is this check necessary or will there always be tags even when empty array?
       const postTags = post.tags && post.tags
        // console.log('postTags: ', postTags)
        postTags.forEach(tag => {
            if (!tagsObj[tag.name]) {
                tagsObj[tag.name] = 1
            } else {
                tagsObj[tag.name] = tagsObj[tag.name] + 1
            }
        })
        return tagsObj
    }, {})
    console.log('tagCounts: ', tagCounts)
    return (
        <HeaderLayout>
            <h2>Tags:</h2>
            {/* <KeywordTags 
                tags={tags}
            /> */}

            <h2>Posts:</h2>
            {posts.map(
                ({ 
                    _id, 
                    _createdAt,
                    slug, 
                    title, 
                    description, 
                }) => (
                    <ListItem key={_id}>
                        <Link
                            href='/post/[slug]'
                            as={`/post/${slug.current}`}
                        >
                            <a>{title}</a>
                        </Link>
                        <p>{description}</p>
                        <DateP>
                            {moment.utc(_createdAt).format("LL")}
                        </DateP>
                    </ListItem>
                )
            )}
        </HeaderLayout>
    )
}

Index.getInitialProps = async function() {
    return await client.fetch(`
        *[ _type == "post" ]{
            ..., 
            tags[]->{_id, name}
        }
    `)
}

export default Index
