import styled from 'styled-components'

const TagBtn = styled.button`
    padding: 10px 15px;
    margin: 15px 0 0 15px;
    color: rgb(250, 223, 147);
    font-size: 1.2em;
    border: none;
    background-color: rgb(3, 152, 252);
    font-family: 'Lustria', serif;
    &:hover {
        cursor: pointer;
    }
`
const Span = styled.span`
    margin: 0 5px;
    color: rgb(250, 223, 147);
`

function KeywordTags(props) {
    // console.log('props: ', props)
    const tagCountsArray = []
    for (const [ tag, count] of Object.entries(props.tags)) {
        tagCountsArray.push({
            'name': tag,
            'count': count
        })
    }

    tagCountsArray.sort((a, b) => {
        return (a.name - b.name) 
    })
    console.log('tagCountsArray: ', tagCountsArray)

    return (

        <div>
            {tagCountsArray.map(tag => (
                <TagBtn 
                    key={tag.name}
                    // onClick={}
                >   
                    {tag.name}
                    <Span>({tag.count})</Span>
                </TagBtn>
            ))}
        </div>
    )
}

export default KeywordTags
