import { useState, useEffect } from 'react'
import styled, { ThemeProvider } from 'styled-components'
import { GlobalStyles } from '../components/GlobalStyles'
// import { dark, light } from '../colorScheme'
import modes from '../colorScheme'

function MyApp({ Component, pageProps }) {
    const [ userThemeStr, setUserThemeStr ] = useState('light')
    const userThemeObj = modes[userThemeStr]


    return (
        <ThemeProvider theme={userThemeObj}>
            <GlobalStyles />
            <Component 
                {...pageProps} 
                toggleUserTheme={setUserThemeStr}
                // userTheme={userTheme}
            />
        </ThemeProvider>
    )
}

export default MyApp
