import { useState, useEffect } from 'react'
import styled, { ThemeProvider } from 'styled-components'
import { GlobalStyles } from '../components/GlobalStyles'
import colorScheme from '../colorScheme'

const dark = {
    body: `${colorScheme.darkThemeColorBg}`,
    text: `${colorScheme.darkThemeColorText}`,
    // primaryColor: `${colorScheme.primaryColor}`,
    primaryColor: `${colorScheme.primaryColorDarker}`,
    secondaryColor: `${colorScheme.secondaryColor}`,
    tertiaryColor: `${colorScheme.tertiaryColor}`,
}

const light = {
    body: `${colorScheme.lightThemeColorBg}`,
    text: `${colorScheme.lightThemeColorText}`,
    // primaryColor: `${colorScheme.primaryColor}`,
    primaryColor: `${colorScheme.primaryColorDarker}`,
    secondaryColor: `${colorScheme.secondaryColor}`,
    tertiaryColor: `${colorScheme.tertiaryColor}`,
}

function MyApp({ Component, pageProps }) {
    const [ userTheme, setUserTheme ] = useState('light')

    return (
        <ThemeProvider 
            theme={dark}
            themeString='dark'
        >
            <GlobalStyles />
            <Component {...pageProps} />
        </ThemeProvider>
    )
}

export default MyApp
