import client from '../client'
import Link from 'next/link'
import { useState } from 'react'
// import useSWR from 'swr'
import styled from 'styled-components'
import moment from 'moment'
import HeaderLayout from '../components/HeaderLayout'
import KeywordTags from '../components/KeywordTags'
import '../globalStyles.css'

const ListItem = styled.li`
    margin: 15px 0 0 15px;
    padding: 10px 15px;
    font-size: 1.75em;
    border: 1px solid rgb(250, 223, 147);
`
const DateP = styled.p`
    font-size: .75em;
`

function Index({ posts }) {
    const [ filteredKeywords, setFilteredKeywords ] = useState([])
    const [ filteredPosts, setFilteredPosts ] = useState([])

    async function getFilteredPosts() {
        const posts = await client.fetch(`
            *[ _type == "post" && tag.name in $filteredKeywords]{
                ..., 
                tags[]->{_id, name}
            }
        `, { filteredKeywords })
        console.log('posts: ', posts)
        setFilteredPosts(posts)
    }

    function handleTagFilter(e) {
        const selectedKeyword = e.target.firstChild.data
        // console.log('selectedKeyword: ', selectedKeyword)
        if (!filteredKeywords.includes(selectedKeyword)) {
            setFilteredKeywords([...filteredKeywords, selectedKeyword])
        } else {
            setFilteredKeywords(filteredKeywords.filter(keyword => keyword !== selectedKeyword))
        }
        console.log('filteredKeywords: ', filteredKeywords)
        getFilteredPosts()
    }

    // TODO: there's gotta be a more straightforward way to get these counts, right? Maybe just do it from query?
    const tags = posts.reduce((tagCount, post) => {
        // TODO: is this check necessary or will there always be tags even when empty array?
        post.tags && post.tags.forEach(tag => {
            if (!tagCount[tag.name]) {
                tagCount[tag.name.count] = 1
            } else {
                tagCount[tag.name] = tagCount[tag.name.count] + 1
            }
        })
        return tagCount
    }, {})

    const tagCountsArray = []
    for (const [ tag, count ] of Object.entries(tags)) {
        tagCountsArray.push({
            'name': tag,
            count,
            _id
        })
    }

    return (
        <HeaderLayout>
            <h2>Tags:</h2>
            <KeywordTags 
                tags={tagCountsArray}
                handleTagFilter={handleTagFilter}
            />

            <h2>Posts:</h2>
            {posts.map(
                ({ 
                    _id, 
                    _createdAt,
                    description, 
                    slug,
                    title, 
                }) => (
                    <ListItem key={_id}>
                        <Link
                            href='/post/[slug]'
                            as={`/post/${slug.current}`}
                        >
                            <a>{title}</a>
                        </Link>
                        <p>{description}</p>
                        <DateP>
                            {moment.utc(_createdAt).format("LL")}
                        </DateP>
                    </ListItem>
                )
            )}
        </HeaderLayout>
    )
}

export async function getStaticProps() {
    const posts = await client.fetch(`
        *[ _type == "post" ]{
            ..., 
            tags[]->{_id, name}
        }
    `)

    return { props: { posts } }
}

export default Index
