import { useState, useEffect } from 'react'
import styled from 'styled-components'
import '../globalStyles.css'

const TagBtn = styled.button`
    padding: 10px 15px;
    margin: 15px 0 0 15px;
    color: var(--secondary-color);
    font-size: 1.2em;
    border: none;
    background-color: var(--primary-color);
    font-family: 'Lustria', serif;
    &:hover {
        cursor: pointer;
    }
`
function getTagCountsData() {
    return Promise.all(
        tags.map(tag => {
            return client.fetch(`
                *[ _id == $tagID ]{
                    name,
                    _id,
                    "tagCount": count(
                        *[ _type == "post" && $tagID in tags[]._ref ]
                    )
                }[0]
            `, { tagID: tag._id })
        })
    )
}

function KeywordTags(props) {
    const [ tagCounts, setTagCounts ] = useState([])
    console.log('props: ', props)

    useEffect(() => {
        getTagCountsData()
            .then(tagCounts => {
                setTagCounts(tagCounts)
            })
            .catch(err => console.log('error getting tag counts: ', err))
        // tags.forEach(async tag => {
        //     const count = await client.fetch(`
        //         count(*[ _type == "post" && $tagID in tags[]._ref ])
        //     `, { tagID: tag._id })
        //     // this check is in case i've added a tag in sanity studio but haven't assigned it to a post yet:
        //     if (count > 0) {
        //         const tagCount = {
        //             _id: tag._id,
        //             name: tag.name,
        //             count
        //         }
        //         // TODO: what's a good way to set these all these at once??
        //         setTagCounts(state => [...state, tagCount])
        //     }
        // })
    }, [])

    tagCounts.sort((a, b) => {
        return (a.name > b.name) ? 1 : -1
    })

    return (
        <div>
            <TagBtn
                onClick={props.handleTagFilter}
                id='clearFilter'
                className={props.filteredTags.length > 0 
                    ? ''
                    : 'selectedTag'
                }
            >
                All Posts
            </TagBtn>

            {props.tags.map(tag => (
                // console.log('tagtag: ', tag)
                tag.count > 0 && (
                    <TagBtn 
                        key={tag.name}
                        id={tag._id}
                        onClick={props.handleTagFilter}
                        className={
                            props.filteredTags.includes(tag._id) 
                                ? 'selectedTag'
                                : ''
                        }
                    >   
                        {tag.name}
                        &nbsp;
                        ({tag.tagCount})
                    </TagBtn>
                )
            ))}
        </div>
    )
    // return null
}

export default KeywordTags
