import { useState, useEffect } from 'react'
import styled, { ThemeProvider } from 'styled-components'
import { GlobalStyles } from '../components/GlobalStyles'
import colorScheme from '../colorScheme'

const darkTheme = {
    body: `${colorScheme.darkThemeColorText}`,
    themePrimaryColor: `${colorScheme.darkThemeColorBg}`,
    primaryColor: `${colorScheme.primaryColor}`,
    secondaryColor: `${colorScheme.secondaryColor}`,
    tertiaryColor: `${colorScheme.tertiaryColor}`,
}

function MyApp({ Component, pageProps }) {
    const [ userTheme, setUserTheme ] = useState('lightMode')

    return (
        <ThemeProvider theme={darkTheme}>
            <GlobalStyles />
            <Component {...pageProps} />
        </ThemeProvider>
    )
}

export default MyApp
