import { useState, useEffect } from 'react'
import styled, { ThemeProvider } from 'styled-components'
import { GlobalStyles } from '../components/GlobalStyles'
import colorSchemes from '../colorSchemes'

const testDarkTheme = {
    body: '#363537',
    themePrimaryColor: '#FAFAFA',
    primaryColor: `${colorSchemes.primaryColor}`,
    secondaryColor: `${colorSchemes.secondaryColor}`,
    tertiaryColor: `${colorSchemes.tertiaryColor}`,
    gradient: 'linear-gradient(#091236, #1E215D)',
}

function MyApp({ Component, pageProps }) {
    const [ userTheme, setUserTheme ] = useState('lightMode')

    return (
        <ThemeProvider theme={testDarkTheme}>
            <GlobalStyles />
            <Component {...pageProps} />
        </ThemeProvider>
    )
}

export default MyApp
